import os


def build_tree(root_dir):
    def build_branch(dirpath):
        branch = {
            'value': os.path.basename(dirpath),
            'label': os.path.basename(dirpath),
            'children': [],
        }
        for sub_dirpath in next(os.walk(dirpath))[1]:
            branch['children'].append(build_branch(os.path.join(dirpath, sub_dirpath)))
        return branch

    root_branch = build_branch(root_dir)
    return root_branch['children']


def getcjOption():
    res = build_tree("../output/retrieval_root")
    res.append(res[1])
    del res[1]
